import com.google.common.collect.ImmutableList;
import lombok.extern.slf4j.Slf4j;
import net.sumaris.core.dao.DatabaseResource;
import net.sumaris.core.dao.data.sample.SampleRepository;
import net.sumaris.core.model.referential.ObjectTypeEnum;
import net.sumaris.core.service.AbstractServiceTest;
import net.sumaris.core.vo.administration.user.DepartmentVO;
import net.sumaris.core.vo.data.ImageAttachmentVO;
import net.sumaris.core.vo.data.OperationVO;
import net.sumaris.core.vo.data.sample.SampleFetchOptions;
import net.sumaris.core.vo.data.sample.SampleVO;
import net.sumaris.core.vo.referential.ReferentialVO;
import org.apache.commons.collections4.CollectionUtils;
import org.junit.*;
import org.springframework.beans.factory.annotation.Autowired;

import java.util.Date;

@Slf4j
public class SampleServiceWriteTest extends AbstractServiceTest {

    @ClassRule
    public static final DatabaseResource dbResource = DatabaseResource.writeDb();


    @Autowired
    private SampleService sampleService;

    @Test
    public void getAndSaveWithImages() {

        // Get an existing sample, with images fetched
        SampleVO sample = sampleService.get(fixtures.getSampleIdWithImages(), SampleFetchOptions.builder()
                .withMeasurementValues(true)
                .withRecorderDepartment(true)
                .withImages(true)
                .build());
        Assert.assertNotNull(sample);
        Assert.assertNotNull(sample.getId());
        Assert.assertTrue(CollectionUtils.isNotEmpty(sample.getImages()));
        Assert.assertEquals(1, CollectionUtils.size(sample.getImages()));
        sample.getImages().forEach(img -> assertImage(img, false));

        // Add a new image
        ImageAttachmentVO image = new ImageAttachmentVO();
        image.setContentType("image/png");
        image.setContent("");

        // Update the image list
        sample.setImages(ImmutableList.<ImageAttachmentVO>builder()
                .addAll(sample.getImages())
                .add(image).build());

        // Save sample, with images
        SampleVO savedVO = sampleService.save(sample);
        Assert.assertNotNull(savedVO);
        Assert.assertNotNull(savedVO.getId());
        Assert.assertTrue(CollectionUtils.isNotEmpty(savedVO.getImages()));
        Assert.assertEquals(CollectionUtils.size(sample.getImages()), CollectionUtils.size(savedVO.getImages()));
        savedVO.getImages().forEach(img -> assertImage(img, true));

    }

    protected void assertImage(ImageAttachmentVO image, boolean checkDateTime) {
        Assert.assertNotNull(image);
        Assert.assertNotNull(image.getId());
        Assert.assertNotNull(image.getUpdateDate());
        Assert.assertNotNull(image.getCreationDate());
        Assert.assertEquals(ObjectTypeEnum.SAMPLE.getId(), image.getObjectTypeId());
        Assert.assertNotNull(image.getObjectId());

        if (checkDateTime) {
            Assert.assertNotNull("Image date should be fill with the sample date", image.getDateTime());
        }
    }
}
