query.select:
COALESCE(
    MAX(CASE SM.PMFM_FK WHEN &pmfmId%pmfmalias% THEN
        (CASE SM.NUMERICAL_VALUE WHEN 1 THEN UPPER('Y') WHEN 0 THEN UPPER('N') END)
        ELSE NULL END
    ),
    MAX(CASE SM2.PMFM_FK WHEN &pmfmId%pmfmalias% THEN
        (CASE SM2.NUMERICAL_VALUE WHEN 1 THEN UPPER('Y') WHEN 0 THEN UPPER('N') END)
        ELSE NULL END
    )
)

COALESCE(
    MAX(CASE SM.PMFM_FK WHEN &pmfmId%pmfmalias% THEN
        TO_CHAR(TIMESTAMP(REPLACE(REPLACE(SM.ALPHANUMERICAL_VALUE, 'T', ' '), 'Z', '')), 'YYYY-DD-MM HH24:MI:SS')
        ELSE NULL END
    ),
    MAX(CASE SM2.PMFM_FK WHEN &pmfmId%pmfmalias% THEN
            TO_CHAR(TIMESTAMP(REPLACE(REPLACE(SM2.ALPHANUMERICAL_VALUE, 'T', ' '), 'Z', '')), 'YYYY-DD-MM HH24:MI:SS')
        ELSE NULL END
    )
)

COALESCE(
    MAX(CASE SM.PMFM_FK WHEN &pmfmId%pmfmalias% THEN
        TO_CHAR(TO_TIMESTAMP(REPLACE(REPLACE(SM.ALPHANUMERICAL_VALUE, 'T', ' '), 'Z', ''), 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-DD-MM HH24:MI:SS')
        ELSE NULL END
    ),
    MAX(CASE SM2.PMFM_FK WHEN &pmfmId%pmfmalias% THEN
        TO_CHAR(TO_TIMESTAMP(REPLACE(REPLACE(SM2.ALPHANUMERICAL_VALUE, 'T', ' '), 'Z', ''), 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-DD-MM HH24:MI:SS')
        ELSE NULL END
    )
)
